import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const loadCountryByName = createAsyncThunk(
  "details/load-countriesByName",
  async (name, { extra: { client, api } }) => {
    return client.get(api.searchByCountry(name));
    //   .then(({ data }) => dispatch(setCountry(data[0])))
    //   .catch((err) => dispatch(setError(err.message)));
  }
);
export const loadNeighborsByBorder = createAsyncThunk(
  "details/load-NeighborsByBorder",
  async (borders, { extra: { client, api } }) => {
    return client.get(api.filterByCode(borders));
    // .then(({ data }) => dispatch(setNeighbors(data.map((c) => c.name))))
    // .catch(console.error);
  }
);

const initialState = {
  status: "idle",
  currentCountry: null,
  error: null,
  neighbors: [],
};

const detailsSlice = createSlice({
  name: "@@details",
  initialState,
  reducers: {
    clearDetails: () => initialState,
  },
  extraReducers: (builder) => {
    builder
      .addCase(loadCountryByName.fulfilled, (state, action) => {
        state.currentCountry = action.payload.data;
        state.status = "received";
      })
      .addCase(loadNeighborsByBorder.fulfilled, (state, action) => {
        state.neighbors = action.payload.data;
        state.status = "received";
      })

      .addMatcher(
        (action) => action.type.endsWith("/rejected"),
        (state, action) => {
          state.status = "rejected";
          state.error = action.payload || action.error;
        }
      )
      .addMatcher(
        (action) => action.type.endsWith("/pending"),
        (state) => {
          state.status = "loading";
          state.error = null;
        }
      );
  },
});

export const detailsReducer = detailsSlice.reducer;
export const { clearDetails } = detailsSlice.actions;

// !Selectors
export const selectDetails = (state) => state.details;
export const selectCurrentCountry = (state) => state.details.currentCountry;
export const selectNeighbors = (state) => state.details.neighbors;
