import { SET_COUNTRY, SET_LOADING, SET_ERROR } from "./details-actions";

const initialState = {
  status: "idle",
  currentCountry: null,
  error: null,
};

export const detailsReducer = (state = initialState, actions) => {
  switch (actions.type) {
    case SET_LOADING:
      return { ...state, status: "loading", error: null };
    case SET_COUNTRY:
      return { ...state, status: "resolved", currentCountry: actions.payload };
    case SET_ERROR:
      return { ...state, status: "rejected", error: actions.payload };

    default:
      return state;
  }
};
