import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  status: "idle",
  currentCountry: null,
  error: null,
  neighbors: [],
};

const detailsSlice = createSlice({
  name: "@@details",
  initialState,
  reducers: {},
});

export const detailsReducer = detailsSlice.reducer;
