import { SET_SEARCH, SET_REGION, CLEAR_CONTROLS } from "./controls-actions";

const initialState = {
  search: "",
  region: "",
};

export const controlsReducer = (state = initialState, actions) => {
  switch (actions.type) {
    case SET_SEARCH:
      return { ...state, search: actions.payload };
    case SET_REGION:
      return { ...state, region: actions.payload };
    case CLEAR_CONTROLS:
      return initialState;

    default:
      return state;
  }
};
