import { SET_COUNTRIES, SET_ERROR, SET_LOADING } from "./countries-actions";

const initialState = {
  status: "idle", // rejected, loading, received
  list: [],
  error: null,
};

export const countriesReducer = (state = initialState, actions) => {
  switch (actions.type) {
    case SET_COUNTRIES:
      return state;
    case SET_LOADING:
      return { ...state, status: "loading", error: null };
    case SET_ERROR:
      return { ...state, status: "rejected", error: actions.payload };

    default:
      return state;
  }
};
